  "use strict";

  function escapeHtml(input){
    return String(input)
      .replace(/&/g,"&amp;")
      .replace(/</g,"&lt;")
      .replace(/>/g,"&gt;")
      .replace(/\"/g,"&quot;")
      .replace(/'/g,"&#39;");
  }

  function inlineFormat(text){
    var s = escapeHtml(text);
    s = s.replace(/`([^`]+)`/g, function(match, code){
      return "<code>" + escapeHtml(code) + "</code>";
    });
    s = s.replace(/\*\*([^*]+)\*\*/g, "<strong>$1</strong>");
    s = s.replace(/\*([^*]+)\*/g, "<em>$1</em>");
    s = s.replace(/\[([^\]]+)\]\(([^)]+)\)/g, "<a href=\"$2\" target=\"_blank\" rel=\"noopener noreferrer\">$1</a>");
    return s;
  }

  function parseMarkdown(input){
    var text = String(input || "").replace(/\r\n/g,"\n");
    var lines = text.split("\n");
    var out = [];
    var inList = false;
    var inCode = false;
    var codeLines = [];

    function closeList(){
      if(inList){
        out.push("</ul>");
        inList = false;
      }
    }

    for(var i=0;i<lines.length;i++){
      var line = lines[i];
      var trimmed = line.trim();

      if(trimmed.indexOf("```") === 0){
        if(inCode){
          out.push("<pre><code>" + escapeHtml(codeLines.join("\n")) + "</code></pre>");
          codeLines = [];
          inCode = false;
        }else{
          closeList();
          inCode = true;
        }
        continue;
      }

      if(inCode){
        codeLines.push(line);
        continue;
      }

      if(/^\s*[-*]\s+/.test(line)){
        if(!inList){
          closeList();
          out.push("<ul>");
          inList = true;
        }
        out.push("<li>" + inlineFormat(line.replace(/^\s*[-*]\s+/, "")) + "</li>");
        continue;
      }

      closeList();

      if(trimmed === ""){
        out.push("");
        continue;
      }

      var headingMatch = /^\s*(#{1,6})\s+(.*)$/.exec(line);
      if(headingMatch){
        var level = headingMatch[1].length;
        out.push("<h" + level + ">" + inlineFormat(headingMatch[2]) + "</h" + level + ">" );
        continue;
      }

      out.push("<p>" + inlineFormat(line) + "</p>");
    }

    if(inCode){
      out.push("<pre><code>" + escapeHtml(codeLines.join("\n")) + "</code></pre>");
    }
    if(inList){
      out.push("</ul>");
    }

    return out.join("\n");
  }

  function marked(input){
    return parseMarkdown(input);
  }

  marked.parse = parseMarkdown;
  marked.parseInline = inlineFormat;

  global.marked = marked;
