          for(var z = 0; z < j.bnf[i].rhs.length; z++){
            for(var t = 0; t < j.bnf[i].rhs[z][0].length; t++)
              if(j.bnf[i].rhs[z][0][t].name == "EOF" && j.bnf[i].rhs[z][0][t].type == "t") EOFused = true;
          }  
