function removeLeftRecursionGrammar(grammar){
	function ContainsSequence(evalto, seq){
		for (var i = 0; i < evalto.length; i++){
			var s = evalto[i].Seq;
			if (seq.length != s.length) continue;
			var equal = true;
			for (var j = 0; j < s.length; j++){
				if (s[j] !== seq[j]){
					equal = false;
					break;
				}
			}
			if (equal) return true;
		}
		return false;
	}
	var res = removeEpsilonGrammar(grammar); // remove epsilon
	if (res.result == "FAILED") return res; // any problems with removing epsilon?
	var noEpsGrammar = res.grammar.replace(" | EPSILON", ""); // remove remaining rule
	var addEps = (noEpsGrammar != res.grammar); // S -> EPSILON possible?
	res = removeChainsGrammar(noEpsGrammar); // Rebuild BNF without chain rules
	if (res.result == "FAILED") return res; // any problems with removing chains?
	var BNF = parseBNF(res.grammar);
	var lbnf = linkBNF(BNF); // for more comfortable rule access
	var Names = lbnf.NonTerminals.map(n=>n.Name).concat(lbnf.Terminals.map(n=>n.Name));
	// Paulls Algorithm
	var numNT = lbnf.NonTerminals.length; // fix loop limit to original NT
//	console.log("Start: " + BNF2Text(unlinkBNF(lbnf)));
	for (var i = 0; i < numNT; i++){ // iterate all original NT
		var xi = lbnf.NonTerminals[i];
		for (var j = 0; j < i; j++){ // iterate all NT processed before
			var xj = lbnf.NonTerminals[j];
			for (var k = xi.EvalTo.length-1; k >= 0 ; k--){ // iterate rules of NT
				if (xj == xi.EvalTo[k].Seq[0]){ // indirect recursion
					for (var m = 0; m < xj.EvalTo.length; m++){ // take all rules of xj
						// add new rule with rule of first NT expanded by current rule without first NT
						var rule = {Seq: xj.EvalTo[m].Seq.concat(xi.EvalTo[k].Seq.slice(1))};
						if (!ContainsSequence(xi.EvalTo, rule.Seq))
							xi.EvalTo.push(rule);
					}
					xi.EvalTo.splice(k,1); // remove old rule
				}
			}
		}
//		console.log(xi.Name+"-A: " + BNF2Text(unlinkBNF(lbnf)));
		var newNT = undefined;
		for (var k = xi.EvalTo.length-1; k >= 0 ; k--){ // iterate rules of NT
			if (xi == xi.EvalTo[k].Seq[0]){ // direct
				if (newNT == undefined){
					var newname = "N."+xi.Name;
					while(Names.indexOf(newname) != -1) // with a unique name
						newname = "N"+newname; 
					Names.push(newname);
					newNT = {Name: newname, EvalTo: [{Seq:[], Code:""}]}; // with epsilon-rule
					for (var m = 0; m < xi.EvalTo.length; m++){ // all original rules
						if (xi.EvalTo[m].Seq[0] !== xi) // not starting with self-recursion
							xi.EvalTo[m].Seq.push(newNT); // expanded with new NT
					}
					lbnf.NonTerminals.push(newNT);
				}
				// on any occurence -> append the new replacement rule
				var rule = {Seq: xi.EvalTo[k].Seq.slice(1).concat([newNT]),Code:""};
				if (!ContainsSequence(newNT.EvalTo, rule.Seq))
					newNT.EvalTo.push(rule);
				xi.EvalTo.splice(k,1); // remove current rule
			}
		}
//		console.log(xi.Name+"-B: " + BNF2Text(unlinkBNF(lbnf)));
	}
	BNF = unlinkBNF(lbnf);
	if (addEps)
		BNF.bnf.find(b=>b.name == BNF.s).rhs.push([[],""]);
	return {"result":"OK", "grammar":BNF2Text(BNF)};
};
function leftFactorGrammar(grammar){
	var cnt = 0; // number of generated nonterminals
	function ProcessGroupLevel(groups, level, rhs){
		var resGroups = []; // changed groups
		var resNT = []; // new nonterminals
		for (var i = 0; i < groups.length; i++){ // for each group of equal rules (until level)
			var itemDic = {}; // build new sub-groups
			var newNT = undefined; // maybe a new NT is created
			for (var j = 0; j < groups[i].Idx.length; j++){ // check each rule in group
				var item = rhs[groups[i].Idx[j]][0][level]; // get item at process-level
				if (item === undefined){ // no item at this level (=epsilon)
					if (level == 0) // at first level
						groups[i].NT.rhs.push(rhs[groups[i].Idx[j]]); // keep the rule
					else{ // otherwise (occures only once per group)
						cnt++; // a new nt is required for all others in this group
						var newname = "F"+cnt;
						while(Names.indexOf(newname) != -1){
							cnt++;
							newname = "F"+cnt;
						}
						Names.push(newname);
						newNT = {name:newname, rhs:[[[],""]]}; // with epsilon rule only
						var newrhs = [rhs[groups[i].Idx[j]][0].slice(groups[i].Start),""]; // take relevant part of the rule
						newrhs[0].push({name:newNT.name, type:"nt"}); // append newNT
						groups[i].NT.rhs.push(newrhs); // add new rule using new NT
					}
				}
				else { // valid expression item at this level
					if (itemDic[item.name] == undefined) // not occured until here
						itemDic[item.name] = {Seq:groups[i].Seq+item.name, NT:groups[i].NT, Start:groups[i].Start, Idx:[]}; // new sub-group
					itemDic[item.name].Idx.push(groups[i].Idx[j]); // append rule-index
				}
			}
			var newGroups = []; // result groups (input for next level process)
			Object.keys(itemDic).forEach((name)=>{ // for each sub-group
				if (itemDic[name].Idx.length > 1) // real group with more than 1 element
					newGroups.push(itemDic[name]); // processed in next level
				else{ // 1 element >> unique appearance 
					if (level == 0) // at first level
						groups[i].NT.rhs.push(rhs[itemDic[name].Idx[0]]); // keep the rule
					else{ // otherwise
						if (newNT === undefined){ // if there is no newNT already
							cnt++; // create a new one
							var newname = "F"+cnt;
							while(Names.indexOf(newname) != -1){
								cnt++;
								newname = "F"+cnt;
							}
							Names.push(newname);
							newNT = {name:newname, rhs:[]}; // without epsilon rule
							var newrhs = [rhs[itemDic[name].Idx[0]][0].slice(groups[i].Start, level),""]; // take relevant part of the rule
							newrhs[0].push({name:newNT.name, type:"nt"}); // append newNT
							groups[i].NT.rhs.push(newrhs); // add new rule using new NT
						}
						// append rest of current item to newNT
						var newrhs = [rhs[itemDic[name].Idx[0]][0].slice(level),""];
						newNT.rhs.push(newrhs);
					}
				}
			});
			if (newNT != undefined) // if a new NT was created (never in level 0)
				resNT.push(newNT); // deliver it
			for (var j = 0; j < newGroups.length; j++){ // iterate resulting groups
				if (newNT != undefined){ // if a new NT was created
					newGroups[j].NT = newNT; // set it as base for remaining sub-groups
					newGroups[j].Start = level; // remember level for relevant expression parts
				}
				resGroups.push(newGroups[j]); // add to result (input for next level)
			}
		}
		// adapt input array: hold next-level-groups now
		groups.splice(0,groups.length);
		for (var i = 0; i < resGroups.length; i++)
			groups.push(resGroups[i]);
		return resNT;
	}
	function getEnumArray(len){ 
		return Array.apply(null, {length: len}).map(Number.call, Number);
	}
	var BNF = parseBNF(grammar); // get BNF
	cleanupGrammar.RemoveEqualReplacements(BNF); // remove equal rules (disturbing algorithm)
	var Names = BNF.nonterminals.concat(BNF.terminals);
	// create result object (copy without rules)
	var res = { bnf:[], s:BNF.s, nonterminals:BNF.nonterminals, terminals:BNF.terminals, lex:BNF.lex};
	for (var i = 0; i < BNF.bnf.length; i++){ // rebuild rules
		var curNT = BNF.bnf[i];
		var bRes = {name:curNT.name, rhs:[]}; // result nt without rules
		res.bnf.push(bRes); // append to result bnf
		// determine maximum rule length (number of expression items)
		var len = curNT.rhs.reduce((max,r)=> r[0].length > max ? r[0].length : max,0);
		if (len > 0){
			// at the begin there is 1 group, representing bRes, with all rules (all rule idx), beginning all with ""
			var groups = [{Seq:"", NT:bRes, Start:0, Idx:getEnumArray(curNT.rhs.length)}];
			for (var j = 0; j < len; j++){ // iterate until maximum number of expression-items in rules
				var resNT = ProcessGroupLevel(groups,j, curNT.rhs); // process a certain level
				for (var k = 0; k < resNT.length; k++) // iterate new NTs
					res.bnf.push(resNT[k]); // append them
			}
			// Since there are no double-rules >> groups-array is empty here
		}
		else // only EPSILON rule
			bRes.rhs.push([[],""]); // re-append it
	}
	
	return {"result":"OK", "grammar":BNF2Text(res)};
};
function ea2grammar(automaton, eatype){
/*	var res = removeTrapStates(automaton);
	if (res.result != "OK") return res;
	var a = res.automaton; // nea without trap-states*/
	var a = JSON.parse(JSON.stringify(automaton));
	var bnf = { bnf:[], // initialize result grammar
				s:undefined, 
				nonterminals:[], 
				terminals:a.Alphabet};
				
	var addStateToGrammar = function(s){
		bnf.nonterminals.push(s.Name); // add state as nonterminal
		var nt = {name:s.Name, rhs:[]}; // init nt-rules
		bnf.bnf.push(nt);
		for (var j = 0; j < s.Transitions.length; j++){
			var t = s.Transitions[j]; // transitions from current state
			var targetState = a.States.find(ts=>ts.ID == t.Target); // determine target state
			var targetNT = {name:targetState.Name, type:"nt"}; // prepare nonterminal-reference
			for (var k = 0; k < t.Labels.length; k++){ // iterate lables/terminals of transitions
				if (t.Labels[k] == ""){ // epsilon-rule
					nt.rhs.push([[targetNT],""]); // -> targetNT
					if (targetState.Final) // target state is a final state
						nt.rhs.push([[],""]); // add epsilon rule
				}
				else{
					nt.rhs.push([[{name:t.Labels[k],type:"t"},targetNT],""]); // -> label targetNT
					if (targetState.Final) // target state is a final state
						nt.rhs.push([[{name:t.Labels[k],type:"t"}],""]); // -> label
				}
			}
		}
		if (s.Start){ // start-state found
			bnf.s = s.Name; // set start-symbol
			if (s.Final) // is final
				nt.rhs.push([[],""]); // add epsilon rule
		}
	};
				
	for (var i = 0; i < a.States.length; i++){
	  if(a.States[i].Start)
  		addStateToGrammar(a.States[i]);
	}
	for (var i = 0; i < a.States.length; i++){
	  if(!a.States[i].Start)
	  	addStateToGrammar(a.States[i]);
	}
	//cleanupGrammar.RemoveEqualReplacements(bnf)
	return {"result":"OK", "grammar":BNF2Text(bnf)};
};
